public class SherlockValidString {
    @Test
    public void testHealthy() {
        Assert.assertEquals("NO", isValid("aabbccddeefghi"));
        Assert.assertEquals("YES",isValid(""));
        Assert.assertEquals("NO",isValid("aabbcd"));
        Assert.assertEquals("YES",isValid("aabbc"));
        Assert.assertEquals("YES",isValid("aabbcc"));
        Assert.assertEquals("YES",isValid("abcc"));
    }


    static String isValid(String s) {
        Map<Character,Integer> map = new HashMap<>();

        int deletedCount = 0;
        for(int i=0;i<s.length(); i++){
            map.compute(s.charAt(i), (k,v)-> v == null? 1: v+1);
        }

        return "NO";
    }
}
